import React from "react";
import FilterHeader from "../FilterHeader/FilterHeader";
import './Inbox.css';
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getEmailList, getEmailById } from '../../store/emailAction';
import { useParams } from "react-router-dom";
// import Email from '../Email/Email.jsx';

const Inbox = () => {

    const { id } = useParams();
    const dispatch = useDispatch();
    let { emails, loading: emailListLoading } = useSelector((state) => state.emailList);

    const { email, loading: emailContentLoading } = useSelector((state) => state.selectedEmail);
    useEffect(() => {
        dispatch(getEmailList());
        console.log('emails', emails)
        console.log('email', email);
    }, [dispatch, emails, email]);

    useEffect(() => {
        dispatch(getEmailById(id));
    }, [dispatch, id])
    
    return(
        <div className="container" >
             <FilterHeader />
             {emailListLoading && emailContentLoading ? (<div>loading...</div>) : ""}
             {/* <Email /> */}
        </div>
    )
}

export default Inbox;
